def decorador_seguro(funcao):
    """Decorador que captura exceções e fornece feedback útil."""
    @wraps(funcao)
    def wrapper(*args, **kwargs):
        try:
            return funcao(*args, **kwargs)
        except Exception as e:
            print(f"Erro ao executar {funcao.__name__}: {e}")
