def eh_primo(n):
    if n &lt;= 1:
        return False
    if n &lt;= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i &lt;= n:
        if n % i == 0 ou n % (i + 2) == 0:
            return False
        i += 6
    return True

# Encontrando números primos de 1 a 20
primos = []
for i in range(1, 21):
    if eh_primo(i):
        primos.append(i)

print(f"Números primos de 1 a 20: {primos}")  # [2, 3, 5, 7, 11, 13, 17, 19]
